import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Box } from "@mui/material";
import "../styles/styles.css";

function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      if (scrollPosition > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <AppBar position="fixed" sx={{ background: "black" }}>
      <Toolbar>
        <Typography className="nav-brand" variant="body3" component="div">
          <Link to="/">
            <img
              className={`nav-logo ${isScrolled ? "hide" : ""}`}
              src="/assets/logo-white.png"
              alt="Logo"
            />
          </Link>
          <span
            style={{
              marginTop: "20px",
              visibility: isScrolled ? "hidden" : "visible",
            }}
          >
            Enhance the audio...Enhance your expectations
          </span>
        </Typography>
        <Box className={`nav-items ${isScrolled ? "show" : "hide"}`}>
          <Typography variant="h6" component="div">
            <Link className="nav-item" to="/profile">
              View Profile
            </Link>
            <Link className="nav-item" to="/">
              Home
            </Link>
            <Link className="nav-item" to="/JoinUs">
              JoinUs
            </Link>
            <Link className="nav-item" to="/upload">
              Upload
            </Link>
            <Link className="nav-item" to="/UpgradePlan">
              UpgradePlan
            </Link>
            <Link className="nav-item" to="/AboutUs">
              AboutUs
            </Link>
          </Typography>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
