import React, { useState } from "react";
import { Button, Typography, Box } from "@mui/material";
import AccountInformation from "./AccountInformation"; // Import AccountInformation component
import PaymentInformation from "./PaymentInformation"; // Import PaymentInformation component

function ViewProfile() {
  const [showAccountInfo, setShowAccountInfo] = useState(false);
  const [showPaymentInfo, setShowPaymentInfo] = useState(false);

  const toggleAccountInfo = () => {
    setShowAccountInfo(!showAccountInfo);
    setShowPaymentInfo(false); // Ensure only one module is displayed at a time
  };

  const togglePaymentInfo = () => {
    setShowPaymentInfo(!showPaymentInfo);
    setShowAccountInfo(false); // Ensure only one module is displayed at a time
  };

  return (
    <Box
      sx={{
        maxWidth: 600,
        margin: "auto",
        marginTop: 10,
        padding: 2,
        border: "1px solid #ccc",
        borderRadius: 8,
      }}
    >
      <Typography variant="h4" align="center" gutterBottom>
        View Your Profile
      </Typography>
      {/* Account Information Module */}
      <Button onClick={toggleAccountInfo} sx={{ marginBottom: 2 }}>
        Account Information
      </Button>
      {showAccountInfo && <AccountInformation />}

      {/* Payment Information Module */}
      <Button onClick={togglePaymentInfo} sx={{ marginBottom: 2 }}>
        Payment Information
      </Button>
      {showPaymentInfo && <PaymentInformation />}
    </Box>
  );
}

export default ViewProfile;
