import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Box } from "@mui/material";
import "../styles/styles.css";

function Navbar() {
  const [showNavItems, setShowNavItems] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.pageYOffset;
      setShowNavItems(currentScrollPos > 50); // Adjust the scroll threshold as needed
    };

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <AppBar position="fixed" sx={{ background: "black" }}>
      <Toolbar>
        <Typography className="nav-brand" variant="body3" component="div">
          <Link to="/">
            <img
              className="nav-logo"
              src="/assets/logo-white.png"
              alt="Logo"
              style={{ visibility: showNavItems ? "hidden" : "visible" }}
            />
          </Link>
          <span
            style={{
              marginTop: "20px",
              visibility: showNavItems ? "hidden" : "visible",
            }}
          >
            Enhance the audio...Enhance your expectations
          </span>
        </Typography>
        <Box
          className="nav-items"
          style={{ visibility: showNavItems ? "visible" : "hidden" }}
        >
          <Typography variant="h6" component="div">
            <Link className="nav-item" to="/profile">
              View Profile
            </Link>
            <Link className="nav-item" to="/">
              Home
            </Link>
            <Link className="nav-item" to="/JoinUs">
              JoinUs
            </Link>
            <Link className="nav-item" to="/upload">
              Upload
            </Link>
            <Link className="nav-item" to="/UpgradePlan">
              UpgradePlan
            </Link>
            <Link className="nav-item" to="/AboutUs">
              AboutUs
            </Link>
          </Typography>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
