import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Link,
} from "@mui/material";
import {
  GoogleLoginButton,
  FacebookLoginButton,
} from "react-social-login-buttons";
import "../styles/styles.css";

function JoinUS() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDob] = useState("");
  const [password, setPassword] = useState("");
  const [verifyPassword, setVerifyPassword] = useState("");
  const [errors, setErrors] = useState({});

  const handleToggleMode = () => {
    setIsSignUp((prevState) => !prevState);
    setErrors({});
  };

  const validateInputs = () => {
    const errors = {};
    if (!username.trim()) {
      errors.username = "Username is required";
    }
    if (!password.trim()) {
      errors.password = "Password is required";
    } else if (!/^(?=.*[a-zA-Z])(?=.*\d).{6,}$/.test(password)) {
      errors.password =
        "Password must be alphanumeric with minimum 6 characters";
    }
    if (isSignUp) {
      if (!email.trim()) {
        errors.email = "Email is required";
      }
      if (!dob.trim()) {
        errors.dob = "Date of birth is required";
      }
      if (password !== verifyPassword) {
        errors.verifyPassword = "Passwords do not match";
      }
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const checkUsernameAvailability = () => {
    // Simulate checking if the username is available
    const isUsernameAvailable = !username.includes("used"); // Replace with your validation logic
    return isUsernameAvailable;
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    if (validateInputs()) {
      // Proceed with sign-in logic
      console.log("Sign-in successful");
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (validateInputs()) {
      if (!checkUsernameAvailability()) {
        setErrors({ username: "Username is already registered" });
        return;
      }
      // Proceed with sign-up logic
      console.log("Sign-up successful");
    }
  };

  return (
    <div>
      <Container
        maxWidth="sm"
        style={{
          marginLeft: "10%",
          width: "80%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
          minHeight: "100vh",
        }}
      >
        <div style={{ marginTop: "2rem" }}>
          <Typography variant="h4" gutterBottom>
            {isSignUp ? "Sign Up" : "Sign In"}
          </Typography>
          <form
            style={{ width: "100%", marginTop: "1rem" }}
            noValidate
            autoComplete="off"
            onSubmit={isSignUp ? handleSignUp : handleSignIn}
          >
            {/* Rest of the form code */}
          </form>
        </div>
      </Container>
    </div>
  );
}

export default JoinUS;
