import React from "react";
import { useHistory } from "react-router-dom"; // Import useHistory hook from React Router
import "../styles/styles.css";

function UpgradePlan() {
  const history = useHistory(); // Get the history object

  // Function to handle redirection to payment page
  const redirectToPayment = (plan) => {
    history.push(`/payment/${plan}`); // Redirect to the payment page with the selected plan
  };

  return (
    <div className="upgrade-plan-container">
      <h2 className="upgrade-plan-heading">Upgrade your Plan</h2>
      <div className="payment-plans">
        <div
          className="payment-plan"
          onClick={() => redirectToPayment("platinum")}
        >
          <div className="plan-header">Platinum Plan</div>
          <div className="plan-price">$99/month</div>
          <h5> Unlimited files Upload</h5>
          <h5>Upload Up to 20GB</h5>
        </div>
        <div className="payment-plan" onClick={() => redirectToPayment("gold")}>
          <div className="plan-header">Gold Plan</div>
          <div className="plan-price">$79/month</div>
        </div>
        <div
          className="payment-plan"
          onClick={() => redirectToPayment("silver")}
        >
          <div className="plan-header">Silver Plan</div>
          <div className="plan-price">$59/month</div>
        </div>
        <div className="payment-plan" onClick={() => redirectToPayment("free")}>
          <div className="plan-header">Free Plan</div>
          <div className="plan-price">Limited Features</div>
        </div>
      </div>
    </div>
  );
}

export default UpgradePlan;
