import React, { useState } from "react";
import { TextField, Button, Typography, Box, Avatar } from "@mui/material";
import { Link } from "react-router-dom";

function ViewProfile() {
  const [userInfo, setUserInfo] = useState({
    email: "user@example.com",
    password: "password",
    dateOfBirth: "2000-01-01",
    comments: "Enter your comments here...",
    avatar: "/assets/user-avatar.png",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted!");
    console.log("Updated user information:", userInfo);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prevUserInfo) => ({
      ...prevUserInfo,
      [name]: value,
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUserInfo((prevUserInfo) => ({
        ...prevUserInfo,
        avatar: reader.result,
      }));
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleDeleteAccount = () => {
    // Implement logic to delete the account here
    console.log("Account deleted!");
  };

  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        margin: "auto",
        marginTop: "2rem",
        position: "relative",
        overflow: "hidden",
        backgroundColor: "#f0f0f0", // Background color
      }}
    >
      <Box
        sx={{
          padding: "20px",
          border: "1px solid #000", // Black border
          borderRadius: 8,
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Black with opacity
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
        }}
      >
        <Typography variant="h4" align="center" gutterBottom>
          View Your Profile
        </Typography>
        <Box
          sx={{ display: "flex", justifyContent: "center", marginBottom: 2 }}
        >
          <Avatar
            src={userInfo.avatar}
            alt="User Avatar"
            sx={{ width: 100, height: 100 }}
          />
        </Box>

        {/* Account information form */}
        <form onSubmit={handleSubmit}>{/* Rest of the form */}</form>

        {/* Text button to redirect to payment form */}
        <Box sx={{ textAlign: "center", marginTop: 2 }}>
          {/* Rest of the button */}
        </Box>
      </Box>
    </div>
  );
}

export default ViewProfile;
