import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Importing useHistory from react-router-dom

function JoinUS() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDob] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory(); // Initializing useHistory

  const handleToggleMode = () => {
    setIsSignUp((prevState) => !prevState);
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    // Check the username and password
    if (username === "admin" && password === "admin") {
      // Redirect to admin dashboard
      console.log("Redirecting to admin dashboard...");
      history.push("/admin"); // Redirect to admin dashboard
    } else {
      // Redirect to regular user dashboard or display an error message
      console.log(
        "Redirecting to regular user dashboard or display error message..."
      );
      // Add logic to redirect to regular user dashboard or display error message
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    // Add sign-up logic here
  };

  return (
    <Container maxWidth="xs">
      <div
        style={{
          marginTop: "2rem",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography variant="h4" gutterBottom>
          {isSignUp ? "Sign Up" : "Sign In"}
        </Typography>
        <form
          style={{ width: "100%", marginTop: "1rem" }}
          noValidate
          autoComplete="off"
          onSubmit={isSignUp ? handleSignUp : handleSignIn}
        >
          {/* Your form fields */}

          <Button
            variant="contained"
            color="primary"
            fullWidth
            style={{ marginTop: "1rem" }}
            type="submit"
          >
            {isSignUp ? "Sign Up" : "Sign In"}
          </Button>
        </form>
        {/* Other components */}
      </div>
    </Container>
  );
}

export default JoinUS;
