import React, { useState } from "react";
import { Box, Typography, TextField, Button, MenuItem } from "@mui/material";

function FileUpload() {
  const [selectedAudioFile, setSelectedAudioFile] = useState(null);
  const [selectedMidiFile, setSelectedMidiFile] = useState(null);
  const [selectedFormat, setSelectedFormat] = useState("");

  const handleAudioFileChange = (event) => {
    setSelectedAudioFile(event.target.files[0]);
  };

  const handleMidiFileChange = (event) => {
    setSelectedMidiFile(event.target.files[0]);
  };

  const handleFormatChange = (event) => {
    setSelectedFormat(event.target.value);
  };

  const handleUpload = () => {
    // Handle file upload logic
    console.log("Audio file uploaded:", selectedAudioFile);
    console.log("MIDI file uploaded:", selectedMidiFile);
    console.log("Selected format:", selectedFormat);
  };

  return (
    <Box
      sx={{
        height: "100vh",
        maxWidth: 400,
        margin: "auto",
        marginTop: 10,
        padding: 2,
        "& > *": {
          marginBottom: 2,
        },
      }}
    >
      <Typography variant="h4" align="center" gutterBottom color="black">
        File Upload
      </Typography>
      <input
        type="file"
        accept="audio/*"
        onChange={handleAudioFileChange}
        style={{ display: "none" }}
        id="audio-file-input"
      />
      <input
        type="file"
        accept=".mid,.midi"
        onChange={handleMidiFileChange}
        style={{ display: "none" }}
        id="midi-file-input"
      />
      <Box>
        <label htmlFor="audio-file-input">
          <Button variant="contained" component="span" fullWidth>
            {selectedAudioFile ? "Audio File Selected" : "Select Audio File"}
          </Button>
        </label>
      </Box>
      <Box>
        <label htmlFor="midi-file-input">
          <Button variant="contained" component="span" fullWidth>
            {selectedMidiFile ? "MIDI File Selected" : "Select MIDI File"}
          </Button>
        </label>
      </Box>
      <TextField
        select
        fullWidth
        label="Select Format"
        value={selectedFormat}
        onChange={handleFormatChange}
        margin="normal"
        // color="white"
      >
        <MenuItem value="format1">WAV</MenuItem>
        <MenuItem value="format2">UpScaling</MenuItem>
        <MenuItem value="format3">SVS</MenuItem>
      </TextField>
      <Button
        variant="contained"
        color="primary"
        fullWidth
        onClick={handleUpload}
        disabled={!selectedAudioFile || !selectedMidiFile || !selectedFormat}
      >
        Upload
      </Button>
    </Box>
  );
}

export default FileUpload;
