import React, { useState } from "react";
import { Typography, Box, Button, Grid } from "@mui/material";

// Dummy data for uploaded files (replace with actual data from backend)
const uploadedFilesData = [
  {
    id: 1,
    fileName: "example_audio.mp3",
    fileType: "audio",
    uploader: "John Doe",
    likes: 10,
    shares: 5,
  },
  {
    id: 2,
    fileName: "example_video.mp4",
    fileType: "video",
    uploader: "Jane Smith",
    likes: 15,
    shares: 8,
  },
];

function UploadedFiles() {
  // State to keep track of uploaded files
  const [uploadedFiles, setUploadedFiles] = useState(uploadedFilesData);

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Uploaded Files
      </Typography>
      <Grid container spacing={2}>
        {uploadedFiles.map((file) => (
          <Grid item key={file.id}>
            <Box>
              <Typography variant="subtitle1">{file.fileName}</Typography>
              <Typography variant="body2">
                Uploaded by: {file.uploader}
              </Typography>
              <Typography variant="body2">Likes: {file.likes}</Typography>
              <Typography variant="body2">Shares: {file.shares}</Typography>
              <Button
                variant="outlined"
                color="primary"
                onClick={() => handleDownload(file)}
              >
                Download
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                onClick={() => handleShare(file)}
              >
                Share
              </Button>
              <Button
                variant="outlined"
                color="default"
                onClick={() => handleLike(file)}
              >
                Like
              </Button>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

function Home() {
  return (
    <Box
      sx={{
        height: "100vh", // Set height to 100vh
        display: "flex",
        flexDirection: "column",
        justifyContent: "top",
        alignItems: "center",
      }}
    >
      <Typography
        variant="h5"
        sx={{
          fontFamily: "Rubik",
          fontSize: "4rem",
          color: "lightblack",
          marginBottom: "20px",
        }}
      >
        Welcome To SuperSonics
      </Typography>
      {/* Render the FileUpload component */}
      <FileUpload />
      {/* Render the UploadedFiles component */}
      <UploadedFiles />
    </Box>
  );
}

export default Home;
