import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Link,
} from "@mui/material";
import {
  GoogleLoginButton,
  FacebookLoginButton,
} from "react-social-login-buttons";
import "../styles/styles.css";

function JoinUS() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleToggleMode = () => {
    setIsSignUp((prevState) => !prevState);
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    // Check the username and password
    if (username === "admin" && password === "admin") {
      // Redirect to admin dashboard
      console.log("Redirecting to admin dashboard...");
      // Add logic to redirect to admin dashboard
    } else {
      // Redirect to regular user dashboard
      console.log("Redirecting to regular user dashboard...");
      // Add logic to redirect to regular user dashboard
    }
  };

  return (
    <Container maxWidth="xs">
      <div
        style={{
          marginTop: "2rem",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography variant="h4" gutterBottom>
          {isSignUp ? "Sign Up" : "Sign In"}
        </Typography>
        <form
          style={{ width: "100%", marginTop: "1rem" }}
          noValidate
          autoComplete="off"
          onSubmit={handleSignIn}
        >
          {!isSignUp && (
            <>
              <TextField
                className="form-input"
                fullWidth
                id="username"
                label="Username"
                variant="outlined"
                margin="normal"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <TextField
                className="form-input"
                fullWidth
                id="password"
                label="Password"
                type="password"
                variant="outlined"
                margin="normal"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </>
          )}
          <Button
            variant="contained"
            color="primary"
            fullWidth
            style={{ marginTop: "1rem" }}
            type="submit"
          >
            {isSignUp ? "Sign Up" : "Sign In"}
          </Button>
        </form>
        <Typography variant="body2" style={{ marginTop: "1rem" }}>
          Or sign in with:
        </Typography>
        <Grid
          container
          spacing={2}
          justifyContent="center"
          style={{ marginTop: "0.5rem" }}
        >
          <Grid item xs={6}>
            <GoogleLoginButton onClick={() => console.log("Google clicked")} />
          </Grid>
          <Grid item xs={6}>
            <FacebookLoginButton
              onClick={() => console.log("Facebook clicked")}
            />
          </Grid>
        </Grid>
        <Typography variant="body2" style={{ marginTop: "1rem" }}>
          <Link href="#" onClick={() => console.log("Forgot password clicked")}>
            Forgot password?
          </Link>
        </Typography>
        <Typography variant="body2" style={{ marginTop: "1rem" }}>
          {isSignUp ? "Already have an account? " : "Don't have an account? "}
          <Link href="#" onClick={handleToggleMode}>
            {isSignUp ? "Sign in" : "Sign up"}
          </Link>
        </Typography>
      </div>
    </Container>
  );
}

export default JoinUS;
