import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Link,
} from "@mui/material";
import {
  GoogleLoginButton,
  FacebookLoginButton,
} from "react-social-login-buttons";
import "../styles/styles.css";

function JoinUS() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDob] = useState("");
  const [password, setPassword] = useState("");
  const [verifyPassword, setVerifyPassword] = useState("");
  const [errors, setErrors] = useState({});

  // Define the array of existing usernames here
  const existingUsernames = ["user1", "user2", "user3"];

  const handleToggleMode = () => {
    setIsSignUp((prevState) => !prevState);
    setErrors({});
  };

  const validateInputs = () => {
    const errors = {};
    if (!username.trim()) {
      errors.username = "Username is required";
    } else if (existingUsernames.includes(username.trim())) {
      errors.username = "Username is already taken";
    }
    if (!password.trim()) {
      errors.password = "Password is required";
    } else if (!/\d/.test(password)) {
      errors.password = "Password must contain at least one numeric character";
    }
    if (isSignUp) {
      if (!email.trim()) {
        errors.email = "Email is required";
      }
      if (!dob.trim()) {
        errors.dob = "Date of birth is required";
      }
      if (password !== verifyPassword) {
        errors.verifyPassword = "Passwords do not match";
      }
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    if (validateInputs()) {
      // Proceed with sign-in logic
      console.log("Sign-in successful");
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (validateInputs()) {
      // Proceed with sign-up logic
      console.log("Sign-up successful");
    }
  };

  return (
    <div className="blurry-background">
      <Container maxWidth="sm">
        <div
          style={{
            marginTop: "2rem",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography variant="h4" gutterBottom>
            {isSignUp ? "Sign Up" : "Sign In"}
          </Typography>
          <form
            style={{ width: "100%", marginTop: "1rem" }}
            noValidate
            autoComplete="off"
            onSubmit={isSignUp ? handleSignUp : handleSignIn}
          >
            {/* Form fields */}

            <Button
              variant="contained"
              color="primary"
              fullWidth
              style={{ marginTop: "1rem" }}
              type="submit"
            >
              {isSignUp ? "Sign Up" : "Sign In"}
            </Button>
          </form>
          {/* Other UI elements */}
        </div>
      </Container>
    </div>
  );
}

export default JoinUS;
