import React, { useRef, useState } from "react";
import { Typography, Box, Container } from "@mui/material";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";
import FeedbackCard from "./FeedbackCard";
import { IconButton } from "@mui/material";

function AboutUs() {
  const scrollRef = useRef(null);
  // Assuming you have a state to hold comments received from the comment section
  const [comments] = useState([
    {
      comment: "Comment 1",
      name: "User 1",
      jobTitle: "Job Title 1",
      imageUrl: "/assets/image1.jpg",
    },
    {
      comment: "Comment 2",
      name: "User 2",
      jobTitle: "Job Title 2",
      imageUrl: "/assets/image2.jpg",
    },
    // Add more comments as needed
  ]);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft -= 200; // Adjust the scroll distance as needed
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += 200; // Adjust the scroll distance as needed
    }
  };

  return (
    <Container sx={{ maxWidth: "xl", padding: "0" }}>
      <Box
        sx={{
          marginTop: "50px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Typography
          variant="h1"
          sx={{
            fontFamily: "Rubik",
            fontSize: "6rem",
            color: "lightblack",
          }}
        >
          About Us
        </Typography>
      </Box>

      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          gap: "16px",
          padding: "16px",
        }}
      >
        <IconButton onClick={scrollLeft}>
          <KeyboardArrowLeft />
        </IconButton>
        <Box
          className="feedback-section"
          ref={scrollRef}
          sx={{
            display: "flex",
            gap: "16px",
            padding: "16px",
            width: "100%",
            overflowX: "auto",
            scrollbarWidth: "none",
            msOverflowStyle: "none",
            "&::-webkit-scrollbar": {
              display: "none",
            },
            "&:hover > *": {
              transition: "transform 0.5s ease",
            },
            "& > *:hover": {
              transform: "scale(1.05)", // Scale up when card is hovered
            },
          }}
        >
          {comments.map((comment, index) => (
            <FeedbackCard
              key={index}
              comment={comment.comment}
              name={comment.name}
              jobTitle={comment.jobTitle}
              imageUrl={comment.imageUrl}
            />
          ))}
        </Box>
        <IconButton onClick={scrollRight}>
          <KeyboardArrowRight />
        </IconButton>
      </Box>
    </Container>
  );
}

export default AboutUs;
