import React, { useState } from "react";
import { TextField, Button, Typography, Box, Avatar } from "@mui/material";
import { Link } from "react-router-dom"; // Import Link from react-router-dom

function ViewProfile() {
  const [userInfo, setUserInfo] = useState({
    email: "user@example.com",
    password: "password",
    dateOfBirth: "2000-01-01",
    comments: "Enter your comments here...",
    avatar: "/assets/user-avatar.png", // Default user avatar image
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted!");
    console.log("Updated user information:", userInfo);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prevUserInfo) => ({
      ...prevUserInfo,
      [name]: value,
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUserInfo((prevUserInfo) => ({
        ...prevUserInfo,
        avatar: reader.result,
      }));
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleDeleteAccount = () => {
    console.log("Account deleted!");
  };

  return (
    <Box
      sx={{
        maxWidth: 400,
        margin: "auto",
        marginTop: 10,
        padding: 2,
        border: "1px solid #ccc",
        borderRadius: 8,
      }}
    >
      <Typography variant="h4" align="center" gutterBottom>
        View Your Profile
      </Typography>
      <Box sx={{ display: "flex", justifyContent: "center", marginBottom: 2 }}>
        <Avatar
          src={userInfo.avatar}
          alt="User Avatar"
          sx={{ width: 100, height: 100 }}
        />
      </Box>
      <input
        type="file"
        accept="image/*"
        id="avatar-input"
        style={{ display: "none" }}
        onChange={handleAvatarChange}
      />
      <label htmlFor="avatar-input">
        <Button variant="contained" component="span">
          Change Profile Picture
        </Button>
      </label>
      <form onSubmit={handleSubmit}>{/* Existing form fields */}</form>

      {/* Text button to redirect to payment form */}
      <Box sx={{ textAlign: "center", marginTop: 2 }}>
        <Link to="/payment" style={{ textDecoration: "none" }}>
          <Button variant="outlined">Go to Payment Form</Button>
        </Link>
      </Box>
    </Box>
  );
}

export default ViewProfile;
