import React, { useRef } from "react";
import { Typography, Box, Container } from "@mui/material";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";
import FeedbackCard from "./FeedbackCard";
import { IconButton } from "@mui/material";

function Home() {
  const scrollRef = useRef(null);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft -= 200; // Adjust the scroll distance as needed
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += 200; // Adjust the scroll distance as needed
    }
  };

  return (
    <Container sx={{ maxWidth: "100%", padding: "0" }}>
      <Box
        sx={{
          marginTop: "50px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Typography
          variant="h1"
          sx={{
            fontFamily: "Rubik",
            fontSize: "6rem",
            color: "lightblack",
          }}
        >
          Welcome To SuperSonics
        </Typography>
      </Box>

      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          gap: "16px",
          padding: "16px",
        }}
      >
        <IconButton onClick={scrollLeft}>
          <KeyboardArrowLeft />
        </IconButton>
        <Box
          className="feedback-section"
          ref={scrollRef}
          sx={{
            display: "flex",
            gap: "16px",
            padding: "16px",
            width: "100%",
            overflowX: "auto",
            scrollbarWidth: "none",
            msOverflowStyle: "none",
            "&::-webkit-scrollbar": {
              display: "none",
            },
            "&:hover > *": {
              transition: "transform 0.5s ease",
            },
            "& > *:hover": {
              transform: "scale(1.05)", // Scale up when card is hovered
            },
          }}
        >
          <FeedbackCard
            comment="Lorem ipsum dolor sit amet consectetur adipisicing elit. Quia, tempora voluptates. Voluptatem deserunt consequatur minus, incidunt explicabo velit molestiae nam tenetur esse numquam delectus natus! Fugiat explicabo accusantium doloribus architecto."
