import React, { useRef } from "react";
import { Typography, Box, Container } from "@mui/material";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";
import FeedbackCard from "./FeedbackCard";
import { IconButton } from "@mui/material";
import ContactForm from "./ContactForm";

function AboutUs({ userComment }) {
  const scrollRef = useRef(null);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft -= 200;
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += 200;
    }
  };

  return (
    <Container sx={{ maxWidth: "xl", padding: "0" }}>
      <Box
        sx={{
          marginTop: "50px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography
          variant="h1"
          sx={{
            fontFamily: "Rubik",
            fontSize: "6rem",
            color: "lightblack",
            marginBottom: "20px",
          }}
        >
          About Us
        </Typography>
        <ContactForm />
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "16px",
            padding: "16px",
            overflowX: "auto",
            scrollbarWidth: "none",
            msOverflowStyle: "none",
            "&::-webkit-scrollbar": {
              display: "none",
            },
          }}
        >
          <IconButton onClick={scrollLeft}>
            <KeyboardArrowLeft />
          </IconButton>
          <Box
            className="feedback-section"
            ref={scrollRef}
            sx={{
              display: "flex",
              gap: "16px",
              padding: "16px",
              width: "100%",
              flexWrap: "nowrap",
              "& > *:last-child": {
                marginRight: 0,
              },
            }}
          >
            <FeedbackCard
              comment={userComment}
              name="Omar Zakzook"
              jobTitle="web designer"
              imageUrl="/assets/DSC00537.jpg"
            />
            <FeedbackCard
              comment={userComment}
              name="Mahmoud mamdouh"
              jobTitle="Software Engineer"
              imageUrl="/assets/Screenshot 2024-03-02 at 15.25.28.png"
            />
            <FeedbackCard
              comment={userComment}
              name="Maged Magdy"
              jobTitle="Software Engineer"
              imageUrl=""
            />
            <FeedbackCard
              comment={userComment}
              name="Ahmed Abdelfattah"
              jobTitle="Product manager"
              imageUrl="/assets/Screenshot 2024-03-02 at 15.25.03.png"
            />
          </Box>
          <IconButton onClick={scrollRight}>
            <KeyboardArrowRight />
          </IconButton>
        </Box>
      </Box>
    </Container>
  );
}

export default AboutUs;
