import React, { useState } from "react";
import { TextField, Button, Typography, Box, Avatar } from "@mui/material";
import { Link } from "react-router-dom";
import AboutUs from "./AboutUs"; // Import the AboutUs component

function ViewProfile() {
  const [userInfo, setUserInfo] = useState({
    email: "user@example.com",
    password: "password",
    dateOfBirth: "2000-01-01",
    comments: "Enter your comments here...",
    avatar: "/assets/user-avatar.png",
  });
  <AboutUs userComment={userInfo.comments} />;
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted!");
    console.log("Updated user information:", userInfo);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prevUserInfo) => ({
      ...prevUserInfo,
      [name]: value,
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUserInfo((prevUserInfo) => ({
        ...prevUserInfo,
        avatar: reader.result,
      }));
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleDeleteAccount = () => {
    // Implement logic to delete the account here
    console.log("Account deleted!");
  };

  return (
    <Box
      sx={{
        maxWidth: 400,
        margin: "auto",
        marginTop: 10,
        padding: 2,
        border: "1px solid #ccc",
        borderRadius: 8,
      }}
    >
      <Typography variant="h4" align="center" gutterBottom>
        View Your Profile
      </Typography>
      <Box sx={{ display: "flex", justifyContent: "center", marginBottom: 2 }}>
        <Avatar
          src={userInfo.avatar}
          alt="User Avatar"
          sx={{ width: 100, height: 100 }}
        />
      </Box>

      <input
        type="file"
        accept="image/*"
        id="avatar-input"
        style={{ display: "none" }}
        onChange={handleAvatarChange}
      />
      <label htmlFor="avatar-input">
        <Button variant="contained" component="span">
          Change Profile Picture
        </Button>
      </label>

      {/* Account information form */}
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Email"
          type="email"
          name="email"
          value={userInfo.email}
          onChange={handleInputChange}
          margin="normal"
          sx={{ marginBottom: 2, color: "gray" }}
        />
        <TextField
          fullWidth
          label="Password"
          type="password"
          name="password"
          value={userInfo.password}
          onChange={handleInputChange}
          margin="normal"
          sx={{ marginBottom: 2, color: "gray" }}
        />
        <TextField
          fullWidth
          label="Date of Birth"
          type="date"
          name="dateOfBirth"
          value={userInfo.dateOfBirth}
          onChange={handleInputChange}
          InputLabelProps={{
            shrink: true,
          }}
          margin="normal"
          sx={{ marginBottom: 2, color: "white" }}
        />
        <TextField
          fullWidth
          label="Comments"
          multiline
          rows={4}
          name="comments"
          value={userInfo.comments}
          onChange={handleInputChange}
          margin="normal"
          sx={{ marginBottom: 2, color: "white" }}
        />
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: 2,
          }}
        >
          <Button variant="contained" type="submit" sx={{ marginRight: 1 }}>
            Save Changes
          </Button>
          <Button
            variant="contained"
            color="error"
            onClick={handleDeleteAccount}
          >
            Delete Account
          </Button>
        </Box>
      </form>

      {/* Text button to redirect to payment form */}
      <Box sx={{ textAlign: "center", marginTop: 2 }}>
        <Link to="/PaymentForm" style={{ textDecoration: "none" }}>
          <Button
            variant="outlined"
            sx={{
              width: "100%", // Make the button wide
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              paddingRight: 2, // Add right padding for the arrow
              backgroundColor: "black", // Set background color to black
              color: "white", // Set text color to white
            }}
          >
            Go to Payment Form
            <span style={{ marginLeft: "auto" }}>→</span> {/* Arrow symbol */}
          </Button>
        </Link>
      </Box>
    </Box>
  );
}

export default ViewProfile;
